mapper.select:
SELECT rlr.* FROM R_LOGISTICS_RFID rlr  WHERE rlr.RFID_STATE<>4 

	AND rlr.RFID_NO LIKE CONCAT('%',#{condition.rfidNo},'%') 


	AND APPLY_NO = #{condition.applyNo}


	AND rlr.PURCHASE_NO LIKE CONCAT('%',#{condition.purchaseNo},'%') 


	AND rlr.CONTRACT_NO LIKE CONCAT('%',#{condition.contractNo},'%') 


	AND rlr.MEMBER_NO = #{condition.memberNo}


	AND rlr.TYPE = #{condition.type}


	AND rlr.PROGRESS_STATE = #{condition.progressState}


	AND rlr.RFID_STATE = #{condition.rfidState}


	AND rlr.IMPORT_DATE >=CONCAT(#{condition.startImportDate}," 00:00:00" ) AND rlr.IMPORT_DATE <= CONCAT(#{condition.endImportDate}," 23:59:59" )

 ORDER BY rlr.RFID_NO DESC

SELECT rlr.* FROM R_LOGISTICS_RFID rlr,M_CONTRACT c WHERE rlr.RFID_STATE<>4 AND rlr.CONTRACT_NO=c.CONTRACT_NO AND rlr.PROGRESS_STATE>=2

	AND rlr.RFID_NO LIKE CONCAT('%',#{condition.rfidNo},'%') 


	AND rlr.PURCHASE_NO LIKE CONCAT('%',#{condition.purchaseNo},'%') 


	AND APPLY_NO = #{condition.applyNo}


	AND rlr.CONTRACT_NO LIKE CONCAT('%',#{condition.contractNo},'%') 


	AND (c.MEMBER_ID_A = #{condition.memberNo} or c.MEMBER_ID_B = #{condition.memberNo})


	AND rlr.TYPE = #{condition.type}


	AND rlr.PROGRESS_STATE = #{condition.progressState}


	AND rlr.RFID_STATE = #{condition.rfidState}


	AND rlr.IMPORT_DATE >=#{condition.startImportDate} AND rlr.IMPORT_DATE <= #{condition.endImportDate}

 ORDER BY rlr.RFID_NO DESC

SELECT rlr.* FROM R_LOGISTICS_RFID rlr WHERE rlr.RFID_STATE<>4

	AND rlr.RFID_NO LIKE CONCAT('%',#{condition.rfidNo},'%') 


	AND rlr.PURCHASE_NO LIKE CONCAT('%',#{condition.purchaseNo},'%') 


	AND APPLY_NO = #{condition.applyNo}


	AND rlr.CONTRACT_NO LIKE CONCAT('%',#{condition.contractNo},'%') 


	AND rlr.MEMBER_NO = #{condition.memberNo} 


	AND rlr.TYPE = #{condition.type}


	AND rlr.PROGRESS_STATE <=1


	AND rlr.PROGRESS_STATE = #{condition.progressState}


	AND rlr.RFID_STATE = #{condition.rfidState}


	AND rlr.IMPORT_DATE >=#{condition.startImportDate} AND rlr.IMPORT_DATE <= #{condition.endImportDate}

 ORDER BY rlr.RFID_NO DESC

