mapper.select:
SELECT tmp.*,CASE WHEN (gCount > 0 AND pCount > 0 AND paCount > 0 AND sCount > 0) THEN 2 WHEN 
(gCount = 0 AND pCount = 0 AND paCount = 0 AND sCount = 0) THEN 0 ELSE 1 END AS STATE FROM (SELECT ID,WINDOW_ID,PROJECT_ID,PROJECT_NAME,ITEM_ID,MEMBER_NO,FILE_PATH,WINDOW_CODE,QUANTITY,SERIES,WINDOW_FACA,WIDTH,HEIGHT,HTML_DATA,
(SELECT COUNT(*) FROM S_GLASS g WHERE g.DOC_ID = d.ID AND g.HEIGHT <> '' AND g.QUANTITY <> '' AND g.WIDTH <> '' AND g.TYPE <> '') AS gCount,
(SELECT COUNT(*) FROM S_P_PRODUCT p WHERE p.DOC_ID = d.ID AND p.HEIGHT <> '' AND p.NAME <> '' AND p.QUANTITY <> '' AND p.WIDTH <> '' ) AS pCount,
(SELECT COUNT(*) FROM S_PARTS pa WHERE pa.DOC_ID = d.ID AND pa.`NAME` <> '' AND pa.QUANTITY <> '' AND pa.TECHONLOGY <> '' AND pa.TYPE <> '' AND pa.UNIT <> '' AND pa.USED <> '') AS paCount,
(SELECT COUNT(*) FROM S_SCIENCE s WHERE s.DOC_ID = d.ID AND s.LENGTH <> '' AND s.NAME <> '' AND s.QUANTITY <> '' AND s.REMARK <> '' AND s.TYPE <> '' AND s.USED <> '' ) AS sCount
 FROM S_DOC d WHERE 1 = 1

	 and d.ITEM_ID =#{condition.itemId}


	 AND d.WINDOW_CODE LIKE CONCAT('%',#{condition.windowCode},'%')


	 AND d.IS_SHOW =#{condition.isShow}


	 and d.SERIES =#{condition.series}


   and d.ID IN 
	
		#{item}
	


   and d.ITEM_ID IN 
	
		#{item}
	

  ORDER BY d.ID DESC 
) AS tmp

  GROUP BY 
	
		${item}

SELECT * FROM S_DOC WHERE 1=1  


	 AND ITEM_ID IN 
	
		#{item}

SELECT ID,WINDOW_ID,PROJECT_ID,PROJECT_NAME,MEMBER_NO,FILE_PATH,WINDOW_CODE,SERIES from S_DOC d WHERE 1 = 1

	 and d.ITEM_ID =#{condition.itemId}


   and d.ID IN 
	
	#{item}
	


   and d.ITEM_ID IN 
	
	#{item}
