mapper.select:
SELECT * FROM M_CONTRACT_BATCH WHERE 1=1 


	AND CONTRACT_ID = #{condition.contractId}


	AND RFID_NO = #{condition.rfidNo}


	AND BATCH_NO = #{condition.batchNo}


	AND UPDATE_STATE != #{condition.state}

 ORDER BY BATCH_NO ASC

		
			SELECT C.ID,C.BATCH_NO,C.AMOUNT,C.BATCH_ITEMS,C.RFID_NO,1 as TYPE FROM M_CONTRACT_BATCH C WHERE  C.RFID_NO=#{condition.rfidNo} UNION ALL 
 			SELECT M.ID,M.BATCH_NO,M.AMOUNT,M.BATCH_ITEMS,M.RFID_NO, 2 as TYPE FROM M_CONTRACT_MODIFY_BATCH M  WHERE  M.RFID_NO=#{condition.rfidNo} UNION ALL 
			SELECT R.ID,R.BATCH_NO,R.AMOUNT,R.BATCH_ITEMS,R.NEW_RFID_NO AS RFID_NO,3 AS TYPE FROM M_CONTRACT_REPLENISH_BATCH R WHERE R.NEW_RFID_NO=#{condition.rfidNo}

		
			SELECT C.ID,C.BATCH_NO,C.AMOUNT,C.BATCH_ITEMS,C.RFID_NO,1 as TYPE FROM M_CONTRACT_BATCH C WHERE C.UPDATE_STATE !=1 AND C.RFID_NO=#{condition.rfidNo} UNION ALL 
 			SELECT M.ID,M.BATCH_NO,M.AMOUNT,M.BATCH_ITEMS,M.RFID_NO, 2 as TYPE FROM M_CONTRACT_MODIFY_BATCH M   WHERE M.UPDATE_STATE !=1 AND M.RFID_NO=#{condition.rfidNo} UNION ALL 
			SELECT R.ID,R.BATCH_NO,R.AMOUNT,R.BATCH_ITEMS,R.NEW_RFID_NO AS RFID_NO,3 AS TYPE FROM M_CONTRACT_REPLENISH_BATCH R WHERE R.NEW_RFID_NO=#{condition.rfidNo}
